mapper.select:
SELECT num, img_name, title, event_date, event_url, end_event
FROM coffee_event
WHERE num = #{num}

mapper.update:
UPDATE coffee_event
SET

	title=#{title}, event_date=#{event_date}, event_url=#{event_url}, img_name=#{img_name}


	title=#{title}, event_date=#{event_date}, event_url=#{event_url}

WHERE num=#{num}
